mapper.update:
 
		 
			UPDATE TB_BOARD 
			SET HIT_CNT = ifnull(HIT_CNT, 0) + 1 
			WHERE IDX = #{IDX}

mapper.select:
  
 	SELECT IDX, HIT_CNT, CREA_ID, CREA_DTM, TITLE, CONTENTS 
	 FROM TB_BOARD 
	 WHERE IDX = #{IDX}

mapper.update:
 
		 
			UPDATE TB_BOARD 
			SET TITLE = #{TITLE},
			    CONTENTS = #{CONTENTS}
			WHERE IDX = #{IDX}

mapper.delete:
 
		 
			UPDATE TB_BOARD 
			SET DEL_GB = 'Y'
			WHERE IDX = #{IDX}
